package bitwiseoperators;

public class BitwiseLeftShift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// b = x<<n
		// b = x*(2^n)
		
		int x = 12;   //1100
		System.out.println("x<<1 = " + (x << 1)); //01000110 =   70<<2 // 1001100
	}                                            //280

}
