package bitwiseoperators;

public class UnsignedRightShift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 20;                                    //00010100 = 20
		System.out.println("x>>>2 = " + (x >>>2));     //00000101 = 5
		                                           
	}

}

